mapper.update:
UPDATE tb_board_free
SET delete_yn = 'y'
WHERE idx = #{idx}
AND password = #{password}

UPDATE tb_board_free
SET title = #{title}, contents = #{contents}
WHERE idx = #{idx}	
AND password = #{password}
AND delete_yn = 'n'

mapper.select:
SELECT COUNT(*)
FROM tb_board_free
WHERE idx = #{idx}
	AND password = #{password}
	AND delete_yn = 'n'
